import {React} from 'react';
import { Link } from 'react-router-dom';

import './Nav.css'


function Nav(props) {
  const { currentUser, handleLogout } = props;

  // let newName = currentUser.name.split(' ').map((name) => name.charAt(0).toUpperCase() + name.slice(1)).join(' ')
  const newName = () => {
    {
      currentUser && (
        <li>Welcome { currentUser.name.split(' ').map((name) => name.charAt(0).toUpperCase() + name.slice(1)).join(' ')}</li>
      )
    }
  }
return (
    <div className="nav">


        <ul className="list">

      <li>Welcome {newName}</li>

          <br/>
          <li><Link to='/users'>Profile</Link></li>
          <br/>
           <li><Link to='/posts/new'>Post Something?</Link></li>
          <br/>
             <li> <button className ="logout" onClick={handleLogout}>Logout</button></li>

            </ul>
            {/* : */}

            {/* <Link to='/'>Login/Register</Link> */}




    </div>
  )

}

export default Nav;
