function Modal(props) {
  const { show, setShow, toggleModal } = props;
  return (
    <div onClick={(e) => setshow(false)}>
      <div className='modal' onClick={(e) => e.stopPropagation()}>
        {props.children}
       </div>

    </div>
  );
}

export default Modal;
