  const [user, setUser] = useState([])
  const { id} = useParams();

  const { currentUser, posts } = props

  console.log(currentUser)
  console.log(posts)

  useEffect(() => {
    const fetchPosts = async (currentUser_id) => {
      const postData = await getUserPost(currentUser.id);
      setUser(postData);
    };
    fetchPosts();
  }, []);

  if (currentUser?.id === posts.user_id) {

    // posts.map(post => {
    //   <>
