import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { getOnePost } from "../../services/posts";
import { destroyComment } from "../../services/comments";
import CreateComment from "../CreateComment";
import { useHistory } from "react-router-dom";
import "./PostDetail.css"
function PostDetail(props) {
  const [post, setPost] = useState("");
  const { id } = useParams();
  const [comments, setComments] = useState([]);
  const params = useParams();
  const history = useHistory();

  console.log(params);

  const { currentUser } = props;
  console.log(currentUser);
  console.log(comments);

  useEffect(() => {
    const fetchPost = async () => {
      const postData = await getOnePost(id);
      setPost(postData);
    };

    fetchPost();
  }, [id]);

  const handleDelete = async (id) => {
    await destroyComment(id);
    setComments((prevState) =>
      prevState.filter((comment) => comment.id !== id)
    );
    history.go(0);
  };
  if (!post) {
    return <h4>Loading...</h4>;
  }

  return (
    <div>
      <div className="post-content">{post.content}</div>
      <p>{post.comments.length} comment</p>

      {post.comments.map((comment) => (
        <React.Fragment key={comment.id}>
          <div className="comments">{comment.content}</div>

          {currentUser?.id === comment.user_id && (
            <div key={comments.id}>

              <button classname="button2"
                onClick={() => {
                  handleDelete(comment.id);
                }}
              >
                delete
              </button>
            </div>
          )}
        </React.Fragment>
      ))}

      <CreateComment postId={post.id} currentUser={currentUser} />
    </div>
  );
}

export default PostDetail;
