import { useState, useEffect, React } from "react";
import { Link } from "react-router-dom";
import Likes from "../components/Likes";
import Modal from "../components/Modal";
import CreatePost from "./CreatePost";
import { getAllComments } from "../services/comments";
import { useParams } from "react-router-dom";

function Posts(props) {
  const [comments, setComments] = useState([]);
  const { posts, setPosts, handleDelete, currentUser } = props;
  const [show, setShow] = useState(false);

  const toggleModal = () => {
    setShow(!show);
  };
  useEffect(() => {
    const fetchComments = async (posts_id) => {
      const commentData = await getAllComments(posts_id);
      setComments(commentData);
    };
    fetchComments();
  }, []);

  console.log(comments.content)
  // const postComments = comments.filter((comment) => {
  //   posts_id === posts.id

  // })
  // console.log(postComments)

  return (
    <div>
      <CreatePost currentUser={currentUser} posts={posts} setPosts={setPosts} />

      <h3>Posts</h3>

      {posts.map((post) => (
        <div key={post.id}>
          <h3>{post.content}</h3>
          {currentUser?.id === post.user_id && (
            <div key={post.id}>
              <button>Like</button>
              <Link to={`/posts/${post.id}`}>comments</Link>
              <Link to={`/posts/${post.id}/edit`}>
                <button>Edit</button>
              </Link>

              <button onClick={() => setShow(post.id)}>delete</button>
            </div>
          )}
          {currentUser?.id !== post.user_id && (
            <>
              <Likes allLikes={post.likes} posts={posts} postsId={posts.id}/>
              <button>Like</button>

              <Link to={`/posts/${post.id}`}>comments({comments.length})</Link>
            </>
          )}
        </div>
      ))}
      {show && (
        <Modal
          posts={posts}
          handleDelate={handleDelete}
          show={show}
          setShow={setShow}
          toggleModal={toggleModal}
        >
          <p>Are you sure?</p>
          <button onClick={() => setShow(false)}>no</button>
          <button
            onClick={() => {
              handleDelete(show);
              setShow(false);
            }}
          >
            yes
          </button>
        </Modal>
      )}
    </div>
  );
}

export default Posts;
