import {React} from 'react';
import { Link } from 'react-router-dom';

import './Nav.css'


function Nav(props) {
  const { currentUser, handleLogout } = props;


return (
    <div className="div">
      <header className="header">
        <Link to='/'><h1>Yak Yik</h1></Link>
        {
          currentUser ?
            <>
              <Link className="nav"to='/users'>Profile</Link>
              <Link className="nav"to='/posts'>Post Something?</Link>
              <p>{currentUser.username}</p>
              <button onClick={handleLogout}>Logout</button>
            </>
            :
            <Link className="nav" to='/login'>Login/Register</Link>

        }
      </header>

    </div>
  )

}

export default Nav;
